import React from "react";

import ReactQuill from "react-quill";
import Theme from "quill/core/theme";
import "react-quill/dist/quill.snow.css";
import styles from "./notearea.module.css";
import 'primereact/resources/themes/lara-light-indigo/theme.css';
import 'primereact/resources/primereact.min.css';
import 'primeicons/primeicons.css';
// import {Editor} from 'primereact/editor'
import Editor from "./quill"
const modules = {
  toolbar: [
    [{ header: [1, 2, 3, 4, 5, 6, false] }],
    [{ font: [] }],
    [{ size: [] }],
    ["bold", "italic", "underline", "strike", "blockquote"],
    [
      { list: "ordered" },
      { list: "bullet" },
      { indent: "-1" },
      { indent: "+1" },
    ],
    ["link", "image"],
  ],
};
const NoteArea = () => {
  return (
    <div className={styles.notearea_wrapper}>
      <div className={styles.noteheadings}>
        <div className="editing_btns">
          <h1> edit me</h1>
        </div>
        <div class={styles.search_container}>
          <input type="text" placeholder="🔍search note" />
          <button type="submit">Search</button>
        </div>
      </div>
      <div className={styles.note_textarea}>
        {/* <input
          className={styles.note_title}
          contenteditable="true"
          placeholder="Title"
        ></input>
        <textarea placeholder="Enter your note here..."></textarea> */}
        <ReactQuill
          theme="snow"
          className={styles.quill_textarea}
          modules={modules}
          
        />
          {/* <Editor className={styles.custom_editor} style={{ height: '380px' }} /> */}
          
      </div>
    </div>
  );
};

export default NoteArea;
