import React from "react";
import "./hero.css";

export const Hero = () => {
  return (
    <div className="hero_wrapper">
      <div className="paddings innerWidth  hero_container ">
 
        <div className="hero_text paddings">
        <div className="hero_title">
        <h1> SUMMAHUB</h1>
        <h2>  <strong>AI Powered:</strong> Text Summarizer and Question
            Generator</h2>
        </div>
        <div className="hero_descr ">
            <p>
            Best tool for helping you Summarize text and generate question with
            note taking functionality
          </p>
        </div>
         
        <button className="getstarted_btn btn_1"> Get Started</button>
        
        </div>
        <div className="hero_pic">
          <div className="img_container">
            <img src="./images/front.png" />
          </div>
        </div>
      </div>
    </div>
  );
};
export default Hero;
