import React from "react";
import styles from "./notearea.module.css";
import ReactQuill from "react-quill";

const modules = {
  toolbar :[
    [{header: [ 1, 2,3,4,5,6,false],
      font:[]
    }]
  ]
}
const NoteArea = () => {
  return (
    <div className={styles.notearea_wrapper}>
      <div className={styles.noteheadings}>
        <div className="editing_btns">
          <h1> edit me</h1>
        </div>
        <div class={styles.search_container}>
          <input type="text" placeholder="🔍search note" />
          <button type="submit">Search</button>
        </div>
      </div>
      <div className={styles.note_textarea}>
        {/* <input
          className={styles.note_title}
          contenteditable="true"
          placeholder="Title"
        ></input> */}
        {/* <textarea placeholder="Enter your note here..."></textarea> */}
        <ReactQuill className={styles.quill_textarea} modules={modules}/>
      </div>
    </div>
  );
};

export default NoteArea;
