import React, { useState } from 'react'
import styles from './sidebar.module.css'
import NoteArea from '../NoteArea/notearea'
const Sidebar = ({setShowNoteArea}) => {
  // const [showNotearea , setShowNoteArea] = useState(false);
  const [activeButton , setActiveButton] = useState("")
 function handleNotesbtn(){
  setShowNoteArea('notes');
  setActiveButton('notes')
 }
 function handleReminderbtn(){
  setShowNoteArea("reminder")
  setActiveButton('reminder')
 }
 function handlenewNotebtn(){
  setShowNoteArea("newnote")
 }
  return (
    <div className={styles.sidebar_wrapper}>
    <div className={styles.addbtn_wrapper}>
    <button  onClick = {handlenewNotebtn} className={styles.addnote_btn}> + New Note</button>
    </div>
    <div className={styles.sidebar_menu}>
    <button  onClick = {handleNotesbtn}><strong className={styles.emoji}> üìÉ</strong>  Notes</button>
    <button  onClick = {handleReminderbtn}><strong className={styles.emoji}> üïí</strong> Reminders </button>
    <button><strong className={styles.emoji}> üß∑</strong> Pinned Notes</button>
    <button><strong className={styles.emoji}> üóëÔ∏è</strong> Trashed </button>

    </div>
      
      
    </div>
  )
}

export default Sidebar
