import React from "react";
import Header from "../HomePage/components/header/header";
import styles from "./question.module.css";

function Question() {
  return (
    <div>
      <Header useButtons={false} />
      <div className={styles.textarea_wrapper}>
        <div className={styles.left_menu}>
        <h1> Difficulty</h1>
        <form>
        <input type="radio" id="hard" name="fav_language" value="Hard" />
        <label for="hard">HTML</label><br></br>
        </form>

        


         </div>
        <div className={styles.left_textarea}>
          <textarea placeholder="Enter your text here ... " className={styles.input_textarea}> </textarea>
          <button className={styles.generate_btn}> Generate </button>
        </div>
        <div className={styles.right_textarea}>
        <textarea placeholder="Enter your text here ... " className={styles.output_textarea}> </textarea>
        </div>
      </div>
     
        
     
    </div>
  );
}
export default Question;
